SILE.settings:declare({ parameter = "languages.eo.ordinal.raisedsuffix", type = "boolean", default = false })
SILE.settings:declare({ parameter = "languages.eo.ordinal.hyphenbefore", type = "boolean", default = false })

SILE.doTexlike([[%
\define[command=tableofcontents:title:eo]{Enhavoj}%
\define[command=book:chapter:pre:eo]{Ĉapitro }%
]])

local function eo_nth(n)
  if n == nil then
    n = ""
  end
  local a, h
  h = SILE.settings:get("languages.eo.ordinal.hyphenbefore") and "-" or ""
  a = h ..
     (SILE.settings:get("languages.eo.ordinal.raisedsuffix") and "ª" or "a")
  return n .. a
end

SILE.registerCommand("book:chapter:post:eo", function()
  SILE.typesetter:typeset(eo_nth())
  SILE.call("medskip", {})
end)

-- Copied from en.lua
local function digits(n)
  local i, ret = -1
  return function()
    i, ret = i + 1, n % 10
    if n > 0 then
      n = math.floor(n / 10)
      return i, ret
    end
  end
end

-- Rezultas la vorton de la postulata nombra parametro
-- Returns the Esperanto name of the required number argument
local eo_string = function (num)
  local words = {"unu", "du", "tri", "kvar", "kvin", "ses", "sep", "ok", "naŭ",  [0] =""}
  local levels = {"mil", "miliono", "miliardo", [0]=""}
  local ten = "dek"
  local hundred = "cent"
  for i=2,8,1 do
    table.insert(levels, 2+i, words[i].."iliono")
    table.insert(levels, 2+i+1, words[i].."iliardo")
  end

  local level = false
  local function getname(pos, dig) --"stateful, but effective." ~ en_string
    level = level or (pos % 3 == 0)
    if(dig == 0) then return "" end
    local dig_name
    if(dig == 1 and pos ~= 0) then
      dig_name = ""
    else
      dig_name = words[dig]
    end
    local name = (pos % 3 == 2 and dig_name .. " " .. hundred .. " " or dig_name .. " ") .. (pos % 3 == 1 and ten .. " " or "")
    if(level) then name, level = name .. levels[math.floor(pos / 3)] .. ((dig > 1 and pos > 5) and "j" or "") .. (pos > 5 and " kaj" or "") .. " ", false end
    return name
  end

  local vword = ""

  for i, v in digits(num) do
    vword = getname(i, v) .. vword
  end

  vword = vword:gsub(" kaj $", "")
  vword = vword:match'^%s*(.*%S)' or ''

  return num == 0 and "nul" or vword
end

SU.formatNumber.eo = {
  string = eo_string,
  nth = eo_nth
}
